package com.syntax.class03;

public class DoubleComparisonHomeWork1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Create a Java program and name it Double Comparison. Declare 2 double values
		 * and if value of first variable is higher then the second, print “Double value
		 * __ is larger than __ .” Otherwise print...
		 */

		double d = 4.5;
		double d1 = 1.3;

		if (d > d1) {
			System.out.println("The Double value " + d + " is larger than the Double value of " + d1);
		} else {
			System.out.println("The double value of " + d1 + " is larger than the Double value of " + d);
		}
	}

}
