package com.syntax.class03;

public class IfConditionDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	int number1=10;
	int number2=20;
	System.out.println("before if condition");
	
	if(true) {
		
		System.out.println(" I am inside if condition");
	}
	
	 System.out.println("after condition");
	 
	 System.out.println("--------------------------------");
	
	 if (number1<number2) {                             // if it's true all will be printed if false nothing will be print
		
		
		System.out.println(" I am inside if condition");
		System.out.println(" I am inside if condition");
	}
	 System.out.println("-----print-------"); // it will print whether the if true or false
	}
	
}
