package com.syntax.class07;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//We use For loop when we know in advance how many times we repeat block of code
	
	/* Say Hi 5 times
	 * 
	 * 
	 */
	
	for(int i=1; i<=5; i++) {
		System.out.println("Hello");
	}
	
	System.out.println(" Print numbers from 1 to 20");
	
for (int i=1; i<=20; i++) {
		
		System.out.print(i+" ");
		
		
	}

System.out.println(" Print numbers from 1 to 20");
System.out.println("");
for (int f=20; f>=1; f--) {

System.out.print(f+" ");
}
}
}