package com.syntax.class07;

public class HomeWorkReviewCal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	
	int i=20;
	
	while(i<=100) {
		System.out.print( i+" ");
	
	i+=2;
	}
	System.out.println("");
	System.out.println(" Another way");
	
	int c=20;
	
	while (c<=100) {
		
		if (c%2==0) {
			System.out.print(c+" ");
		//c++ it will only print 1 time 20 & keep repeating the same.....always Fasle go out & come back
		}
		c++;
	}
	
	}

}
